package com.danycarreto.fragmentsimport android.content.Contextimport android.os.Bundleimport androidx.fragment.app.Fragmentimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport android.widget.Toastimport kotlinx.android.synthetic.main.fragment_discount.*const val ARG_TITLE = "arg_title"class DiscountFragment : Fragment() {    var title:Bundle? = null    lateinit var discountEventListener: DiscountEventListener    override fun onAttach(context: Context) {        super.onAttach(context)        if(context is DiscountEventListener){            discountEventListener = context        }    }    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        arguments?.let {            title = it        }    }    override fun onCreateView(        inflater: LayoutInflater, container: ViewGroup?,        savedInstanceState: Bundle?    ): View? {        return inflater.inflate(R.layout.fragment_discount, container, false)    }    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {        super.onViewCreated(view, savedInstanceState)        WelcomeLabel.text = title?.getString(ARG_TITLE,"")        btnCalculate.setOnClickListener {            tvResult.text = calculateDiscount(edtAmount.text.toString().toDouble(),                edtDiscount.text.toString().toDouble()).toString()            discountEventListener.showResult(tvResult.text.toString())            Toast.makeText(activity, "", Toast.LENGTH_LONG).show()        }    }    fun calculateDiscount(amount:Double, discount:Double) = amount - (amount * (discount/100))    companion object {        fun newInstance(title: String): Fragment {            return DiscountFragment().apply {                arguments = Bundle().apply {                    putString(ARG_TITLE, title)                }            }        }    }}